import React, { useEffect, useState } from 'react'
import Axios from 'axios'
import Notify from 'simple-notify'
import 'simple-notify/dist/simple-notify.min.css'
import HashLoader from 'react-spinners/HashLoader'

export const SaveData = () => {

  const [userdata,setUserdata] = useState([])

  const [deleteData,setDeleteData] = useState({
    id:''
  })

  let yesorno =0;

  useEffect(()=>{
    Axios.get('http://localhost:8000/')
    .then(res=> {
      if(res.status === 200){
          setUserdata(res.data)
          if(res.data.length > 0){
            yesorno = 1
          }
      }
    })
  })

  let loadData;

  if(yesorno)


  if(deleteData.id){
    Axios.delete(`http://localhost:8000/${deleteData.id}`)
    .then(res=> {
      if(res.status === 200){
        new Notify ({
          status: 'error',
          title: 'Success',
          text: '<h6>Task Deleted...!</h6>',
          effect: 'fade',
          speed: 300,
          customClass: '',
          customIcon: '<i class="fa-sharp fa-solid fa-trash" style="color: #ff0000;"></i>',
          showIcon: true,
          showCloseButton: true,
          autoclose: true,
          autotimeout: 3000,
          gap: 20,
          distance: 20,
          type: 1,
          position: 'right top',
          customWrapper: '',
        })
      }else{
        new Notify ({
          status: 'error',
          title: 'Error',
          text: '<h6>Try Again</h6>',
          effect: 'fade',
          speed: 300,
          customClass: '',
          customIcon: '',
          showIcon: true,
          showCloseButton: true,
          autoclose: true,
          autotimeout: 3000,
          gap: 20,
          distance: 20,
          type: 1,
          position: 'right top',
          customWrapper: '',
        })
      }
    })
    setDeleteData({
      id:''
    })
  }
  

  return (
    <div className='col-lg-6' style={{height: '100vh',overflowX:'hidden',}}>
      {loadData}
    </div>
  )
}
