import React, { useEffect, useState } from 'react'
import Axios from 'axios'
import Notify from 'simple-notify'
import 'simple-notify/dist/simple-notify.min.css'
import HashLoader from 'react-spinners/HashLoader'

export const SaveData = () => {

  const [userdata,setUserdata] = useState({
    id:'',
    task:'',
    status:''
  })

  const [deleteData,setDeleteData] = useState({
    id:''
  })

  useEffect(()=>{
    Axios.get('http://localhost:8000/')
    .then(res=> {
      if(res.status === 200){
          if(res.data.length>0){
            setUserdata(res.data)
          }else{
            // setUserdata({
            //   _id:"No Data",
            //   task:"No Data",
            //   status:"No Data"
            // })
          }
      }
    })
  })

  let loadData;

  console.log(userdata);

  if(userdata.length === 0){
      loadData = <div className='row mt-5'>
      <div className='col-lg-12' align='center'>
        <HashLoader color="#ffca2c" />
      </div>
    </div>    
  }else{
    loadData = userdata.map((element) => {
      return (
            <div className='row' key={element._id}>
              <div className='col-lg-1'></div>
              {element.status ? (
                <div className='col-lg-10 mt-3 mb-2' style={{backgroundColor:"#36AE7C",borderRadius:'10px'}}>
                <div className='row mt-3'>
                  <div className='col-lg-12' align='center'>
                    <h5 style={{fontWeight:700}}>{element.task}</h5>
                  </div>
                </div>
                <div className='row mt-3 mb-2'>
                  <div className='col-lg-12' align='center'>
                    <div className='row'>
                      <div className='col-lg-11'></div>
                      <div onClick={()=> setDeleteData({id:element._id})} className='col-lg-1'><i style={{cursor:"pointer"}} className="fa-sharp fa-solid fa-trash"></i></div>
                    </div>
                  </div>
                </div>
              </div>
              ) : (
                <div className='col-lg-10 mt-3 mb-2' style={{backgroundColor:"#ffca2c",borderRadius:'10px'}}>
                <div className='row mt-3'>
                  <div className='col-lg-12' align='center'>
                    <h5 style={{fontWeight:700}}>{element.task}</h5>
                  </div>
                </div>
                <div className='row mt-3 mb-2'>
                  <div className='col-lg-12' align='center'>
                    <div className='row'>
                      <div className='col-lg-9'></div>
                      <div className='col-lg-1'><i style={{cursor:"pointer"}} className="fa-solid fa-circle-check"></i></div>
                      <div className='col-lg-1'><i style={{cursor:"pointer"}} className="fa-solid fa-pen-to-square"></i></div>
                      <div onClick={()=> setDeleteData({id:element._id})} className='col-lg-1'><i style={{cursor:"pointer"}}  className="fa-sharp fa-solid fa-trash"></i></div>
                    </div>
                  </div>
                </div>
              </div>
              )}
            </div>
      );
    });
  }


  if(deleteData.id){
    Axios.delete(`http://localhost:8000/${deleteData.id}`)
    .then(res=> {
      if(res.status === 200){
        new Notify ({
          status: 'error',
          title: 'Success',
          text: '<h6>Task Deleted...!</h6>',
          effect: 'fade',
          speed: 300,
          customClass: '',
          customIcon: '<i class="fa-sharp fa-solid fa-trash" style="color: #ff0000;"></i>',
          showIcon: true,
          showCloseButton: true,
          autoclose: true,
          autotimeout: 3000,
          gap: 20,
          distance: 20,
          type: 1,
          position: 'right top',
          customWrapper: '',
        })
      }else{
        new Notify ({
          status: 'error',
          title: 'Error',
          text: '<h6>Try Again</h6>',
          effect: 'fade',
          speed: 300,
          customClass: '',
          customIcon: '',
          showIcon: true,
          showCloseButton: true,
          autoclose: true,
          autotimeout: 3000,
          gap: 20,
          distance: 20,
          type: 1,
          position: 'right top',
          customWrapper: '',
        })
      }
    })
    setDeleteData({
      id:''
    })
  }
  

  return (
    <div className='col-lg-6' style={{height: '100vh',overflowX:'hidden',}}>
      {loadData}
    </div>
  )
}
